import { useState } from "react";

const checkIfEmpty = (...arg) => arg.map((a) => a.length === 0).includes(true);

const App = () => {
  const [persons, setPersons] = useState([]);
  const [filter, setFiltered] = useState([]);
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");

  const filterPersons = (e) => {
    const text = e.target.value;
    console.log("persons: ", persons);
    const copy = [...persons];

    const filteredPersons = text
      ? copy.filter((person) =>
          person.name.toLowerCase().includes(text.toLowerCase())
        )
      : [...persons];
    setFiltered(filteredPersons);
  };

  const onSubmit = (event) => {
    event.preventDefault();

    const newPerson = {
      name: newName,
      number: newNumber,
      id: persons.length + 1,
    };
    const exist = persons.find(
      (p) => p.name === newName || p.number === newNumber
    );

    if (exist) {
      alert(`${newName} ${newNumber} is already added to phonebook`);
    } else if (checkIfEmpty(newName, newNumber)) {
      alert("Both name and number are required");
    } else {
      setPersons(persons.concat(newPerson));
      setFiltered(persons.concat(newPerson));
      setNewName("");
      setNewNumber("");
    }
  };

  const onChangeName = (event) => {
    setNewName(event.target.value);
  };

  const onChangeNumber = (event) => {
    setNewNumber(event.target.value);
  };

  console.log("persons out: ", JSON.stringify(persons));

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter filterPersons={filterPersons} />

      <h3>Add a new</h3>

      <PersonForm
        onSubmit={onSubmit}
        newName={newName}
        onChangeName={onChangeName}
        newNumber={newNumber}
        onChangeNumber={onChangeNumber}
      />
      <h3>Numbers</h3>
      <Persons persons={filter} />
    </div>
  );
};

export default App;

function Persons({ persons }) {
  return (
    <ul>
      {persons.map((person) => (
        <li key={person.id}>
          {person.name} {person.number}
        </li>
      ))}
    </ul>
  );
}

function Filter({ filterPersons }) {
  return (
    <>
      <div>
        filter shown with: <input onChange={filterPersons} />
      </div>
      <br />
    </>
  );
}

function PersonForm({
  onSubmit,
  newName,
  onChangeName,
  newNumber,
  onChangeNumber,
}) {
  return (
    <form onSubmit={onSubmit}>
      <div>
        name: <input value={newName} onChange={onChangeName} />
      </div>
      <div>
        number: <input value={newNumber} onChange={onChangeNumber} />
      </div>
      <div>
        <button type="submit">add</button>
      </div>
    </form>
  );
}
