import { useState } from "react";

const checkIfEmpty = (...arg) => arg.map((a) => a.length === 0).includes(true);

const App = () => {
  const [persons, setPersons] = useState([{ name: "Arto Hellas" }]);
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");

  const filterPersons = (e) => {
    const text = e.target.value;

    const filteredPersons = text?persons.filter((person) =>
      person.name.toLowerCase().includes(text.toLowerCase())
    ):persons
    setPersons(filteredPersons);
  };

  const onSubmit = (event) => {
    event.preventDefault();

    const newP = { name: newName, number: newNumber, id: persons.length + 1 };
    const exist = persons.find(
      (p) => p.name === newName || p.number === newNumber
    );

    if (exist) {
      alert(`${newName} ${newNumber} is already added to phonebook`);
    } else if (checkIfEmpty(newName, newNumber)) {
      alert("Both name and number are required");
    } else {
      setPersons(persons.concat(newP));
      setNewName("");
      setNewNumber("");
    }
  };

  const onChange = (event) => {
    setNewName(event.target.value);
  };

  const onChangeNumber = (event) => {
    setNewNumber(event.target.value);
  };

  return (
    <div>
      <h2>Phonebook</h2>
      filter shown with: <input onChange={filterPersons} />
      <form onSubmit={onSubmit}>
        <div>
          name: <input value={newName} onChange={onChange} />
          number: <input value={newNumber} onChange={onChangeNumber} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <ul>
        {persons.map((person) => (
          <li key={person.name}>
            {person.name} {person.number}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
